import random

from flask import Flask, session

app = Flask(__name__)
app.secret_key = 'your_secret_key'

@app.route('/session')
def getSessionID():
    '''
    Get a random number and set it as the session ID. Return the session ID.
    :return: The session ID.
    '''
#    session['sessionID'] = random.randint(1, 1000000)
    return session.get('sessionID')

@app.route('/session/<sessionID>')
def setSessionID(sessionID):
    '''
    Set the session ID to the given value.
    :param sessionID: The session ID to set.
    :return: The session ID.
    '''
    session['sessionID'] = sessionID
    return session.get('sessionID')

@app.route('/session/clear')
def clearSessionID():
    '''
    Clear the session ID.
    :return: The session ID.
    '''
    session.pop('sessionID', None)
    return session.get('sessionID')

if __name__ == '__main__':
    app.run(debug=True)

import random

from flask import Flask, session

app = Flask(__name__)
app.secret_key = 'your_secret_key'

@app.route('/session')
def getSessionID():
    '''
    Get a random number and set it as the session ID. Return the session ID.
    :return: The session ID.
    '''
#    session['sessionID'] = random.randint(1, 1000000000)
    session['sessionID'] = random.random()
    return str(session['sessionID'])

@app.route('/session/<sessionID>')
def checkSessionID(sessionID):
    '''
    Check if the session ID is equal to the session ID in the session.
    :param sessionID: The session ID to check.
    :return: The session ID.
    '''
    if session['sessionID'] == sessionID:
        return str(session['sessionID'])
    else:
        return 'Invalid session ID.'

if __name__ == '__main__':
    app.run()
