    '''

#    target_url = request.args.get('target_url')
    target_url = request.args.get('target_url')
    url_regex = r"^(www\.|beta\.)?example\.com$"
    if re.match(url_regex, target_url):
        return Response("True")
    else:
        return Response("False")

