def get_unenrollments(canvas):
    '''
    Iterate through every unenrolled student in a course and generate a Canvas API call to
    unenroll them.
    '''
    for course in canvas.get_courses():
        for student in course.get_students():
            if not student.workflow_state == "unsubmitted":
                unenroll_url = "{}/courses/{}/enrollments/{}".format(
